import { useTheme } from "@mui/material/styles";
import Box from "@mui/material/Box";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import "bootstrap/dist/css/bootstrap.min.css";
import "./style.css";
import Grid from "@mui/material/Grid";
import { useParams } from "react-router-dom";
import Header from "../header/Header";
import { getNftDetails } from "../service/api";
import { useEffect } from "react";

export default function ItemDetail() {
  let { nftId } = useParams();
  console.log(nftId, "params");

  const getNft = async () => {
    const res = await getNftDetails(nftId);
    console.log(res);
  };

  useEffect(() => {
    getNft();
  }, [nftId]);

  const theme = useTheme();

  return (
    <>
      <Header />
      <div className="container  " style={{ paddingTop: "2rem" }}>
        <div className="item_details">
          <div className="row justify-content-center sm:space-y-20">
            <div className="col-lg-4">
              <img
                className="item_img"
                src="https://weaver.moonrank.app/fuak49NIYoRDEaRWf0msN3-a1Tjx7o_q4keia9_cyTY"
                alt=""
              />
            </div>

            <div className="col-lg-6">
              <div className="space-y-20 ">
                <div className="box">
                  <div className="space-y-20">
                    <div className="container ">
                      <h4 className="text-center">
                        <span style={{ fontFamily: "boston", color: "black" }}>
                          {" "}
                          Steven - The Stoned Jungle Nerd
                        </span>
                      </h4>
                    </div>
                    <div className="hr"></div>
                  </div>
                  <br></br>
                  <Box className="text-center">
                    <Grid
                      container
                      rowSpacing={3}
                      columnSpacing={{ xs: 1, sm: 3, md: 3 }}
                    >
                      <Grid item xs={4}>
                        <Card
                          style={{ padding: "10px", backgroundColor: "black" }}
                        >
                          <h6 style={{ color: "white" }}>HEADPIECE</h6>
                          <span style={{ color: "white" }}> Steven</span>
                        </Card>
                      </Grid>
                      <Grid item xs={4}>
                        <Card
                          style={{ padding: "10px", backgroundColor: "black" }}
                        >
                          <h6 style={{ color: "white" }}>SKULL </h6>
                          <span style={{ color: "white" }}>The Stoned</span>
                        </Card>
                      </Grid>
                      <Grid item xs={4}>
                        <Card
                          style={{ padding: "10px", backgroundColor: "black" }}
                        >
                          <h6 style={{ color: "white" }}>TOP</h6>
                          <span style={{ color: "white" }}>Jungle Nerd</span>
                        </Card>
                      </Grid>
                      <Grid item xs={4}>
                        <Card
                          style={{ padding: "10px", backgroundColor: "black" }}
                        >
                          <h6 style={{ color: "white" }}>SHOES</h6>
                          <span style={{ color: "white" }}>Black Boots</span>
                        </Card>
                      </Grid>
                      <Grid item xs={4}>
                        <Card
                          style={{ padding: "10px", backgroundColor: "black" }}
                        >
                          <h6 style={{ color: "white" }}>PANTS</h6>
                          <span style={{ color: "white" }}> Blue Jeans </span>
                        </Card>
                      </Grid>

                      <Grid item xs={4}>
                        <Card
                          style={{ padding: "10px", backgroundColor: "black" }}
                        >
                          <h6 style={{ color: "white" }}>BACKGROUND</h6>
                          <span style={{ color: "white" }}>Blue Velvet </span>
                        </Card>
                      </Grid>
                      <Grid item xs={4}>
                        <Card
                          style={{ padding: "10px", backgroundColor: "black" }}
                        >
                          <h6 style={{ color: "white" }}>ACCESSORY</h6>
                          <span style={{ color: "white" }}>value </span>
                        </Card>
                      </Grid>
                    </Grid>
                  </Box>
                </div>

                <div className="d-flex space-x-20"></div>
                <div className="container p-0 ">
                  <h2 className="text-center">
                    <a
                      style={{
                        fontFamily: "boston",
                        fill: "black",
                        color: "black",
                        backgroundColor: "#f8c307",
                      }}
                      href="#"
                      className="btn w-100 "
                    >
                      <i className="ri-search-line"></i>Download
                    </a>
                  </h2>
                </div>
              </div>
            </div>
          </div>
        </div>
        <br />
        <br />
      </div>
    </>
  );
}
